mapper.insert:
insert into order (o_id, o_petId, o_quantity, 
  o_shipDate, o_status, o_complete
  )
values (#{o_id,jdbcType=INTEGER}, #{o_petId,jdbcType=INTEGER}, #{o_quantity,jdbcType=INTEGER}, 
  #{o_shipDate,jdbcType=DATE}, #{o_status,jdbcType=VARCHAR}, #{o_complete,jdbcType=VARCHAR}
  )

mapper.select:
select o_id, o_petId, o_quantity, o_shipDate, o_status, o_complete
from order
where o_id = #{o_id,jdbcType=INTEGER}
